INSERT INTO `dt_product` (`id`, `category_id`, `name`, `price`, `desc`, `detail`, `img_url`, `create_at`, `qty`, `note`, `sold`, `view`, `active`) VALUES
(1, 1, 'Carl Seiler 133D', '100000000', '<p>Carl Seiler l&agrave; một nh&atilde;n hiệu của h&atilde;ng sản xuất piano Pruthner tại Nhật Bản. Piano Carl Seiler 133D cao 133cm cao hơn size U3 của Yamaha (131cm) cho &acirc;m thanh ấm &aacute;p, vang hay. Thiết kế quyến rũ với m&agrave;u gỗ đỏ, ch&acirc;n cong.<img alt=\"\" src=\"/../../userfiles/images/SinhVienIT_NET---resized-wallpaper-hd-by-sinhvienit-38-.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/../../userfiles/images/ATq0NDj.jpg\" /></p>\r\n', '', 'Carl-Seiler-133D-205x205', '0000-00-00', 20, '', 0, 161, 1),
(2, 1, 'Dan piano Kawai-BL71', '32000000', 'Đàn Piano Kawai BL71 dòng đàn piano cũ chất lượng và đẳng cấp của thương hiệu Kawai Nhật Bản. Dòng piano accoustic được người dùng ưa chuộng hiện nay.\r\nVới chiều cao 132 cm, cao nhất trong số những cây đàn piano đứng (upright), đàn piano Kawai BL71 sẽ mang đến âm thanh vang và độ sáng, độ chi tiết cao, phù hợp cho nhiều thể loại nhạc mà bạn chơi. Đây là sự lựa chọn tốt dành cho học tập của nhiều người hiện nay, xứng đáng với giá trị bỏ ra đầu tư mua đàn piano cũ.', NULL, 'Dan-piano-Kawai-BL71-205x205', '0000-00-00', 30, NULL, 0, 124, 1),
(3, 1, 'Dan Yamaha U2H', '42000000', 'Piano Kawai U2 được sản xuất vào năm 1986, thời kỳ Kawai đạt đến đỉnh cao trong thiết kế và sản xuất đàn piano.\r\nThiết kế với kiểu dáng mạnh mẽ, thiết kế nắp đậy bàn phím mở đứng và giá sách dài rộng giống đàn grand piano.\r\n\r\nÂm sắc của đàn U2 rất giống với đàn grand piano, mang tính đột phá đối với một cây đàn upright rất đặc biệt.', NULL, 'Dan-Yamaha-U2H-205x205', '0000-00-00', 12, NULL, 0, 9, 1),
(4, 1, 'Grand Piano Yamaha', '135000000', '<p>Grand piano Yamaha G2E Đàn Piano Yamaha G2E thuộc dòng đàn Grand Piano, mang vẻ đẹp quy&ecirc;́n rũ, c&oacute; &acirc;m thanh trong s&aacute;ng, k&iacute;ch thước lớn ph&ugrave; hợp với kh&ocirc;ng gian nh&agrave; rộng, biệt thự, sảnh kh&aacute;ch sạn lớn, Đàn Piano Yamaha G2E t&ocirc;n l&ecirc;n vẻ qu&yacute; tộc của người sử dụng. Th&ocirc;ng s&ocirc;́ kỹ thuật: &ndash; Nh&agrave; sản xuất: Piano Yamaha &ndash; Model: G2E &ndash; M&agrave;u: Đen &ndash; Pedals: 03 | Soft Pedal, Mute Pedal, Damper Pedal. &ndash; K&iacute;ch thước: Cao (Height):111 cm , Rộng(Width):151 cm , Ngang(Depth):123 cm &ndash; Trọng lượng: 328 kg &ndash; Chứng từ gốc: CO/CQ &ndash; Bảo h&agrave;nh: 05 Năm &ndash; Dịch vụ k&egrave;m theo: Miễn ph&iacute; vận chuyển tận nơi nội th&agrave;nh, tặng khăn phủ ph&iacute;m, tặng khăn phủ đ&agrave;n, tặng ống sưởi, tặng g&oacute;i dịch vụ tại gia thời hạn 05 năm : Bảo dưỡng m&aacute;y, bảo dưỡng đ&agrave;n, vệ sinh, chỉnh d&acirc;y</p>\r\n', '', 'Grand-Piano-Yamaha-G2E-205x205', '0000-00-00', 10, '', 0, 185, 1),
(5, 1, 'Kawai BL 51', '41000000', 'Đàn piano Kawai BL-61 do thương hiệu đàn piano nổi tiếng Nhật Bản sản xuất với chất lượng hàng đầu trong ngành công nghiệp sản xuất đàn piano.\r\n\r\nĐàn piano Kawai BL-61 hoàn toàn phù hợp cho những khách hàng nào cần một cây đàn piano tốt để chơi nhưng lại đáp ứng được yếu tố thẩm mỹ để trang trí không gian trong nhà. Đàn piano Kawai BL-61 có màu đen huyền bí, chân đàn thẳng với thùng góc cạnh, là cây đàn piano đúng chất cổ điển sẽ thích hợp cho những ai yêu thích vẻ đẹp này.\r\n\r\nĐàn piano Kawai BL-61 được các nghệ nhân tỉ mỉ làm với từng chi tiết, kết hợp giữa những công nghệ tiên tiến và truyền thống, kết hợp với vật liệu chất lượng cao tạo nên cây đàn piano Kawai hoàn hảo.\r\n\r\nĐàn piano Kawai BL-61 sẽ mê hoặc bạn ngay khi tiếng đàn vang lên, bởi âm thanh thực sự tuyệt vời và rất đặc trưng, bàn phím Kawai quá nổi tiếng về độ hay độ tốt làm hài lòng hầu như mọi người.', NULL, 'Kawai-BL-51-205x205', '0000-00-00', 22, NULL, 0, 2, 1),
(6, 1, 'Kawai KU1D', '25000000', 'Piano Kawai KU-1D secondhand', NULL, 'Kawai-KU1D-205x205', '0000-00-00', 30, NULL, 0, 0, 1),
(7, 1, 'Kraus U130', '36000000', 'Piano Kraus U130 được sản xuất tại Nhật bản khoảng thập niên 1980 bởi nhà sản xuất piano Taisei. Đây là nhà sản xuất piano khá nổi tiếng của Nhật Bản với những thương hiệu đàn piano cũ vẫn được tin dùng đến ngày nay như piano Rolex, Gors Kallmann…\r\n\r\nPiano Kraus U130 có chiều cao 131cm tương đương với size U3 của Yamaha nên cho âm sắc vang hay, tiếng bass trầm ấm. Bên cạnh đó, đàn có thiết kế chân cong hài hòa với hai đường họa tiết trên thùng đàn, phù hợp với mọi không gian từ gia đình để học tập giải trí đến sân khấu biểu diễn.', NULL, 'Kraus-U130-205x205', '0000-00-00', 15, NULL, 0, 3, 1),
(8, 1, 'Piano Apollo A7', '52000000', 'Apollo là một thương hiệu của hãng sản xuất đàn piano TOYO Nhật Bản.\r\n\r\nPiano Apollo A7 có chiều cao 132cm cho âm thanh vang, rất hay, tiếng bass dày.', NULL, 'Piano-Apollo-A7-205x205', '0000-00-00', 17, NULL, 0, 1, 1),
(9, 1, 'Piano Atlas A22H', '38000000', 'Piano Atlas là một thương hiệu đàn piano nổi tiếng tại Nhật những năm 1980. Đặc trưng riêng của dòng đàn piano Atlas A22H là âm sắc nghe rất sang trọng. Thiết kế của piano Atlas A22H rất tinh tế và đa dạng.', NULL, 'Piano-Atlas-A22H-205x205', '0000-00-00', 9, NULL, 0, 1, 1),
(10, 1, 'Piano Atlas NA', '28000000', 'Đàn piano Atlas NA-605 được thiết kế dựa theo dòng đàn cơ dạng đứng upright cổ điển .Với chiều cao 131cm tương đương dòng U3 của Yamaha, piano Atlas NA-605 được đánh giá sở hữu âm thanh tinh tế, trầm ấm đáp ứng được nhu cầu của người sử dụng.\r\n\r\nThông số kỹ thuật đàn piano Atlas NA-605\r\n\r\n– Số phím: 88 phím\r\n\r\n– Chất liệu phím: Phenolic Resin\r\n\r\n– Pedal: 3 (Soft Pedal, Mute Pedal, Damper Pedal)\r\n\r\n– Màu sắc: Đen bóng\r\n\r\n– Tình trạng đàn: Đã qua sử dụng\r\n\r\n– Thời gian bảo hành: 5 năm\r\n\r\n– Bảo trì: 5 lần bảo trì miễn phí\r\n\r\n– Vận chuyển: Miễn phí nội thành TP.HCM\r\n\r\n– Trọng lượng: 270 Kg\r\n\r\n– Kích thước: 151 W – 133 H – 63 D\r\n\r\n– Hãng sản xuất: Atlas\r\n\r\n– Xuất xứ: Nhật Bản', NULL, 'Piano-Atlas-NA-605-205x205', '0000-00-00', 15, NULL, 0, 1, 1),
(11, 1, 'Piano Ballindamm BU 30', '26000000', 'Ballindamm là một nhãn hiệu đàn piano thuộc công ty Toyo Piano Nhật Bản.\r\n\r\nĐàn piano Ballindamm BU30 được thiết kế tinh tế với mà vân gỗ cánh gián, chân cong mang dáng vẻ cổ điển. Âm thanh vang hay.', NULL, 'Piano-Ballindamm-BU-30-205x205', '0000-00-00', 19, NULL, 0, 1, 1),
(12, 1, 'Piano Helman 2A', '26000000', 'Helman là thương hiệu piano của hãng TOYO piano Nhật Bản.', NULL, 'Piano-Helman-2A-205x205', '0000-00-00', 22, NULL, 0, 13, 1),
(13, 1, 'Piano Kawai BL61', '41000000', 'Đàn piano Kawai BL-61 do thương hiệu đàn piano nổi tiếng Nhật Bản sản xuất với chất lượng hàng đầu trong ngành công nghiệp sản xuất đàn piano.\r\n\r\nĐàn piano Kawai BL-61 hoàn toàn phù hợp cho những khách hàng nào cần một cây đàn piano tốt để chơi nhưng lại đáp ứng được yếu tố thẩm mỹ để trang trí không gian trong nhà. Đàn piano Kawai BL-61 có màu đen huyền bí, chân đàn thẳng với thùng góc cạnh, là cây đàn piano đúng chất cổ điển sẽ thích hợp cho những ai yêu thích vẻ đẹp này.\r\n\r\nĐàn piano Kawai BL-61 được các nghệ nhân tỉ mỉ làm với từng chi tiết, kết hợp giữa những công nghệ tiên tiến và truyền thống, kết hợp với vật liệu chất lượng cao tạo nên cây đàn piano Kawai hoàn hảo.\r\n\r\nĐàn piano Kawai BL-61 sẽ mê hoặc bạn ngay khi tiếng đàn vang lên, bởi âm thanh thực sự tuyệt vời và rất đặc trưng, bàn phím Kawai quá nổi tiếng về độ hay độ tốt làm hài lòng hầu như mọi người.', NULL, 'Piano-Kawai-BL61-205x205', '0000-00-00', 14, NULL, 0, 1, 1),
(14, 1, 'Piano Kawai US 50', '47000000', 'Series US là dòng đàn cao cấp của hãng piano Kawai.\r\n\r\nPiano Kawai US50 là một trong những dòng piano upright phổ biến và bán chạy nhất của Kawai. Với chiều cao 132cm cùng với công nghệ chế tạo piano tiên tiến, US50 cho âm thanh trong trẻo, vang, tiếng bass nghe rất ấm, cho âm sắc rất gần giống với grand piano.', NULL, 'Piano-Kawai-US-50-205x205', '0000-00-00', 35, NULL, 0, 0, 1),
(15, 1, 'Piano Kawai US60M', '46000000', 'Piano Kawai US60M là phiên bản piano upright giới hạn đặc biệt của hãng Kawai nhân dịp mừng kỷ niệm 60 năm thành lập.\r\nĐược sản xuất vào năm 1987, Kawai US60M thực sự là dòng đàn piano upright chuyên nghiệp với âm sắc chuẩn mực. Theo nhiều chuyên gia thẩm định, âm sắc của Kawai US60M tương đương với dòng grand piano 5’8″ do bảng phát âm thanh (soundboard) có kích thước tương đượng với đàn đại dương cầm cỡ nhỏ (baby grand).\r\n\r\nMột đặc điểm đáng lưu ý là giá sách của Kawai US60M được thiết kế độc lập với nắp đàn (fallboard), với kích thước lớn vừa mang dáng vẻ hiện đại, chắc chắn vừa thuận tiện cho việc để sách nhạc.\r\n\r\nKiểu dáng của Kawai US60M mang phong cách của đàn piano grand với nắp mở dựng đứng, thành hai bên được uống cong.', NULL, 'Piano-Kawai-US60M-205x205', '0000-00-00', 20, NULL, 0, 1, 1),
(16, 1, 'Piano Kawai US 65', '50000000', 'Piano Kawai US65 được sản xuất vào năm 1986, thời kỳ Kawai đạt đến đỉnh cao trong thiết kế và sản xuất đàn piano.\r\nThiết kế với kiểu dáng mạnh mẽ, thiết kế nắp đậy bàn phím mở đứng và giá sách dài rộng giống đàn grand piano.\r\n\r\nÂm sắc của đàn US65 rất giống với đàn grand piano, mang tính đột phá đối với một cây đàn upright rất đặc biệt.', NULL, 'Piano-Kawai-US-65-205x205', '0000-00-00', 20, NULL, 0, 1, 1),
(17, 1, 'Piano Marchen MS 20', '57000000', 'Marchen là một thương hiệu piano Nhật Bản thuộc hãng sản xuất piano nổi tiếng Kawai. Cấu trúc đàn, âm sắc, thiết kế… đều do Kawai sản xuất nên chất lượng đàn Marchen thực sự là chất lượng của đàn Kawai.\r\n\r\nPiano Marchen MS20 có âm sắc rất ấm áp và thanh thoát giống như Kawai nhưng giá rất bình dân. Thiết kế của đàn hài hòa phù hợp với mọi không gian nội thất.', NULL, 'Piano-Marchen-MS-20-205x205', '0000-00-00', 25, NULL, 0, 29, 1),
(18, 1, 'Piano Miki M1F', '60000000', 'Piano Miki M1F thừa hưởng tất cả thiết kế, chất lượng giống như 1 cây đàn Yamaha. Đặc biệt, bộ máy action trên đàn Miki M1F được đóng nhãn Yamaha. Vì vậy, chất lượng của đàn Miki giống hoàn toàn với đàn piano Yamaha nhưng mức rất cực kỳ mềm cho người sử dụng.', NULL, 'Piano-Miki-M1F-205x205', '0000-00-00', 36, NULL, 0, 0, 1),
(19, 1, 'piano yamaha u1e', '32000000', 'Yamaha piano dòng U upright từ lâu đã là một sự lựa chọn hàng đầu cho các tổ chức giáo dục, nhạc sĩ chuyên nghiệp và các nghệ sĩ piano. Nhờ thiết kế toàn diện theo tiêu chuẩn quốc tế cải thiện hiệu suất âm, độ bền của piano. Là dòng đầu tiên đặt ra tiêu chuẩn quốc tế trong sản xuất với những kiểu dáng sang trọng.\r\n\r\nThường có màu đen và rất bóng theo công nghệ Nhật Bản, Piano Yamaha U1E  phù hợp với những đối tượng mới học piano, là sự chọn lựa ưa thích của người tiêu dùng Việt. Với chiều cao 121cm chiếc đàn Piano Yamaha U1E  rất phù hợp với những không gian nhỏ gọn, làm bằng gỗ tự nhiên theo công nghệ Nhật Bản đem tới độ bền lên đến hàng trăm năm cho cây đàn Piano.', NULL, 'piano-yamaha-u1e-205x205', '0000-00-00', 25, NULL, 0, 2, 1),
(20, 1, 'piano yamaha u1f', '34000000', 'Đàn Piano Yamaha dòng U từ lâu đã là một sự lựa chọn hàng đầu cho các tổ chức giáo dục, nhạc sĩ chuyên nghiệp,nghệ sĩ piano,người bắt đầu học piano và biểu diễn bán chuyên nghiệp ở Việt Nam.\r\n\r\nNhờ thiết kế lại toàn diện, sử dụng các vật liệu tiêu chuẩn thế giới chất lượng tốt nhất, piano Yamaha U1F có những tiến bộ rất nhiều trong thiết kế và sản xuất. Thiết kế tinh xảo cho các chi tiết các thành phần cải thiện hiệu suất âm, trong khi các sửa đổi khác làm tăng độ mạnh mẽ và độ bền của đàn. Và nhà sản xuất đặt ra những tiêu chuẩn đặc biệt  khắt khe, kết hợp các tinh túy chuyển thể từ đàn Piano  Yamaha lớn (grand) để chế tác ra các cây đàn dòng U tiêu chuẩn tốt nhất.', NULL, 'piano-yamaha-u1f-205x205', '0000-00-00', 10, NULL, 0, 0, 1),
(21, 1, 'Piano Yamaha U1H', '41000000', 'Đàn Piano Yamaha dòng U từ lâu đã là một sự lựa chọn hàng đầu cho các tổ chức giáo dục, nhạc sĩ chuyên nghiệp, nghệ sĩ piano,người bắt đầu học piano và biểu diễn bán chuyên nghiệp ở Việt Nam.\r\nĐàn Piano Yamaha U1H có thiết kế sang trọng với màu đen bóng, phù hợp với mọi vật liệu nội thất trong nhà, khách sạn,… với âm thanh bay bổng và âm bass dày dặn đã tạo nên một cây đàn thật hoàn hảo.\r\n\r\nMột Số đặc điểm nổi bật trên Yamaha U1H\r\n\r\n1. Thùng đàn Yamaha U1H\r\n\r\nThùng đàn Piano Yamaha U1H có hình dáng giống tủ đứng, được phủ lớp sơn màu đen sáng bóng, góp phần tôn lên vẻ đẹp sang trọng cho cây đàn piano yamaha U1H. Đặc biệt hơn hết, thùng đàn là bộ phần nâng đỡ rất nhiều các bộ phận khác như khung đàn…Nó được làm từ một loại gỗ lớn họ thông.\r\n\r\n2. Pedals U1H\r\n\r\nYamaha U1H có 3 bàn đạp với 3 chức năng khác nhau: soft pedal là giảm âm thanh, tiếp đến là sostenuto pedal là pedal duy trì và cuối cùng là damper pedal (hoặc sustain pedal) là pedal vang âm. Chất liệu của bộ Pedals được đúc từ hợp kim đồng nên rất chắc chắn và sơn màu vàng sáng bóng tạo nên vẻ sang trọng hơn cho cây đàn piano U1H.\r\n\r\n3. Bộ máy Yamaha U1H\r\n\r\nBàn phím piano yamaha U1H gồm có 88 phím, được làm từ chất liệu Phenolic Resin.\r\n\r\nBộ máy phía trong đàn là một tổ hợp liên kết của các bộ phận nhỏ lại với nhau. Nguyên lý hoạt động giống như đòn bẩy và đặc điểm dễ dàng nhận biết là búa đàn. Và hầu hết các chi tiết trong máy đàn đều được chế tạo thủ công và bằng gỗ nên rất nhảy cảm với nhiệt độ và độ ẩm.\r\n\r\nTuy nhiên, giải pháp ống sưởi của các chuyên gia kỹ thuật piano đã giải quyết cho bài toán sử dụng piano tại khí hậu nóng ẩm tại Việt Nam.\r\n\r\n4. Dây đàn U1H\r\n\r\nDây đàn Yamaha U1H được chế tạo bằng thép cường độ cao, hội tụ yếu tố dẻo dai, ít cacbon nhất, dây có đường kính khác nhau. Tổng số lượng dây đàn trên cây piano U1H là 220 dây.\r\n\r\n5. Soundboard Yamaha U1H\r\n\r\nCác soundboard thường được làm từ gỗ họ thông như gỗ Vân Sam Sitka, độ dày của Soundboard vào khoảng ½ inch tương đương 1.27cm, rộng khoảng 4 – 6 inch tương đương 10.16 – 15.24cm và được ghép lại với nhau để tạo thành 1 tấm soundboard lớn.\r\n\r\nBảng cộng hưởng (Soundboard) bao gồm 2 bộ phận chính là: xương sườn (Ribs) và ngựa đàn (bridge)', NULL, 'Piano-Yamaha-U1H-205x205', '0000-00-00', 20, NULL, 0, 0, 1),
(22, 1, 'Piano Yamaha U1M', '50000000', 'Đàn Piano Yamaha từ lâu là thương hiệu piano được ưa chuộng tại Việt Nam bởi chất lượng và uy tín đối với người sử dụng.\r\n\r\nĐàn Piano cơ yamaha U1M – một trong những cây đàn piano được người tiêu dùng bình chọn, yêu thích nhất trong các loại đàn piano Yamaha\r\n\r\nĐàn Piano Yamaha U1M có thiết kế sang trọng với màu đen bóng, phù hợp với mọi vật liệu nội thất trong nhà, khách sạn,… với âm thanh bay bổng và âm bass dày dặn đã tạo nên một cây đàn thật hoàn hảo. \r\nSản phẩm và dịch vụ kèm theo khi mua đàn: miễn phí vận chuyển tận nơi nội thành TP.HCM, tặng ghế, khăn phủ phím, khăn phủ đàn và ống sưởi.\r\n\r\n', NULL, 'Piano-Yamaha-U1M-205x205', '0000-00-00', 14, NULL, 0, 4, 1),
(23, 1, 'Piano Yamaha U2A', '55000000', 'Piano Yamaha U2A được sản xuất từ năm 1982 – 1987 tại Nhật Bản với số seri dao động từ 3.670.000 đến 4.450.000. Vì vậy đàn piano U2A còn rất mới, đặc biệt là hệ thống búa và action còn rất tốt. Âm sắc rất hay nhờ vào công nghệ đỉnh cao trong việc chế tạo piano trong thập niên 1990 của Yamaha', NULL, 'Piano-Yamaha-U2A-205x205', '0000-00-00', 14, NULL, 0, 0, 1),
(24, 1, 'Piano Yamaha U2C', '67000000', 'Với chiều cao trung bình của dòng “upright piano” 127 cm , đàn piano yamaha U2C rất phù hợp cho người mới tập, kể cả trẻ em hay người lớn. Ngoài giai điệu trong sáng, đàn piano cũ  Piano Yamaha U2C còn sở hữu vẻ đẹp từ gỗ gụ đánh bóng sang trọng, tạo điểm nhấn cực kỳ ấn tượng cho căn phòng của bạn.\r\n\r\nThiết kế của dòng Yamaha U2 phù hợp với những ngôi nhà có không gian trung bình (từ 17,5m2 trở lên). Piano đứng Yamaha U2C được chế tác và lắp ráp tại Nhật Bản.', NULL, 'Piano-Yamaha-U2C-205x205', '0000-00-00', 18, NULL, 0, 7, 1),
(25, 1, 'Piano Yamaha U2F', '69000000', 'Đàn piano Yamaha U2F là cây đàn piano chính hãng được sản xuất tại Nhật Bản, U2F là model tiêu chuẩn của piano Yamaha thuộc dòng U2, có màu đen bóng Akira (sử dụng công nghệ sơn gỗ đặc biệt của Nhật Bản), đàn Piano U2F là sự lựa chọn của khá nhiều người tiêu dùng trong nước hiện nay, sử dụng cho học tập và giải trí trong gia đình với mức giá phù hợp.\r\n\r\nDòng U2 sở hữu chiều cao là 127cm, đàn Piano Yamaha U2F rất phù hợp cho người mới chơi đàn piano. Ngoài giai điệu trong sáng, thanh cao, đàn Piano U2F còn sở hữu vẻ đẹp từ gỗ gụ sơn đen bóng sang trọng, tạo nên sự ấn tượng sâu sắc cho căn phòng của bạn.', NULL, 'Piano-Yamaha-U2F-205x205', '0000-00-00', 27, NULL, 0, 9, 1),
(26, 1, 'Piano Yamaha U2G', '70000000', 'Đàn piano Yamaha U2G là model tiêu chuẩn của Piano Yamaha dòng U2, có màu đen ánh sáng Akira (công nghệ sơn gỗ đặc biệt của Nhật Bản), Đàn piano Yamaha U2G là sự lựa chọn của khá nhiều người tiêu dùng trong nước hiện nay.\r\n\r\nVới chiều cao tiêu chuẩn của dòng  Piano Yamaha U2 là 127cm, đàn Piano Yamaha U2H rất phù hợp cho người mới chơi đàn. Ngoài giai điệu trong sáng, Piano Yamaha U2G còn sở hữu vẻ đẹp từ gỗ gụ đánh bóng sang trọng, tạo nên sự ấn tượng cho căn phòng của bạn.', NULL, 'Piano-Yamaha-U2G-205x205', '0000-00-00', 24, NULL, 0, 0, 1),
(27, 1, 'piano yamaha u3a', '72000000', 'Đàn Piano Yamaha U3A là dòng sản phẩm mang đậm chất cổ điển của Yamaha.\r\nPiano Yamaha U3A đã là một đối thủ cạnh tranh lâu dài trong kinh doanh piano trong nhiều thập kỷ. Các thiết kế của cây đàn piano này đã đứng trước thử thách của thời gian . Piano Yamaha U3A là sự lựa chọn hoàn hảo cho không gian nhà, nhà thờ, hoặc trong phòng thu. Là một trong những cây đàn piano nổi tiếng trên thế giới.\r\n\r\nVới công nghệ ngày càng phát triển trong lĩnh vực kỹ thuật, thiết kế và kinh nghiệm. Dòng U đã và luôn là sự chọn lựa sáng giá của những người yêu thích piano, nghệ sĩ, nhạc sĩ và trường nhạc. Giờ đây, dòng U lại có những bước cải tiến mới cho âm thanh tuyệt vời hơn bao giờ hết.\r\n\r\nCải tiến trong thiết kế kích thước và khung sườn đàn :\r\n\r\nĐàn Piano Yamaha U3A chiều cao đến 131 cm tạo ra khoang âm thanh rộng hứa hẹn đem lại cho người dùng âm thanh đúng chuẩn của Piano Yamaha .Cấu trúc sườn đặc biệt tạo nên sự mạnh mẽ hơn cho Soundboard của đàn, trong khi đó, những cột trụ lưng đàn có những cải tiến nhằm tăng độ chắc chắn, tính cộng hưởng cho âm thanh đúng chuẩn của Piano Yamaha\r\n\r\nCải tiến trong thiết búa đàn:\r\n\r\nBúa đàn của dòng U3A YAMAHA được chọn lọc rất đặc biệt để sản sinh cho từng model những tiếng đàn tối ưu, độ nhạy của phím và độ bền cao.\r\n\r\nCải tiến mặt âm cho âm thanh chuẩn và sâu hơn\r\n\r\nSoundboard co giãn và thùng đàn lớn 131 cm là những đặc điểm làm cho âm thanh được mạnh mẽ hơn trong phạm vi rộng với tiếng đàn vô song và cảm giác búa đàn huyền thoại của Yamaha. Với hệ thống Floating Support, soundboard có khả năng tạo độ rung nhiều hơn, làm tăng thêm độ sâu và cá tính cho tiếng bass và mid.\r\nSpruce Soundboard Piano Yamaha U3A\r\n\r\nTại nhà máy gỗ Yamaha và cơ sở chế biến gỗ, vân sam tốt nhất là quarter sawn và ít hơn 10% của tổng số đó được lựa chọn và dành cho soundboards đàn piano Yamaha. Vân sam rắn, chứ không phải là nhiều lớp vân sam hoặc gỗ mềm, là sự lựa chọn trong tất cả các cây đàn piano tốt cho sự khuếch đại của giai điệu âm thanh tốt nhất, và lâu dài.\r\n\r\nFull Length Ribs Piano Yamaha U3A\r\n\r\nYamaha củng cố crown trong soundboards của mình bằng cách sử dụng ribs tiếp tục cạnh của soundboard, và được dán vào lót hình chữ V (hoặc bên trong vành trên cây đàn piano). Tăng cường crown đảm bảo rằng chất lượng âm sẽ duy trì trong nhiều năm và cải thiện sự ổn định điều chỉnh.\r\n\r\nMetal Action Rails Piano Yamaha U3A\r\n\r\nKỹ thuật của Yamaha phát triển nhôm độc đáo Extruded Aluminum Alloy Action Rail (cấp bằng sáng chế Yamaha) là một trong những sáng kiến tốt nhất để cải thiện bộ máy piano trong vòng 100 năm qua. Thông thường biến động trong đường ray gỗ hiệu ứng cảm ứng, gây ra bởi thời tiết thay đổi định kỳ được loại bỏ, cho phép ổn định, kéo dài thời gian sử dụng thông thường của bộ máy.\r\n\r\nBalanced Action Piano Yamaha U3A\r\n\r\nMỗi phím đàn piano Yamaha được thử nghiệm và đo lường cho sự hiệu chỉnh cần thiết để có được thống nhất “giảm trọng lượng” áp lực. Bộ máy Yamaha chơi một cách chính xác và thống nhất. Cân bằng này giúp đảm bảo tuổi thọ của cảm ứng cao cấp và kiểm soát trên bàn phím.\r\n\r\nSpruce Keys Piano Yamaha U3A\r\n\r\nYamaha sử dụng Spruce cho các phím trên tất cả các mô hình cây đàn piano. Spruce là rất nhẹ và sở hữu một tỷ lệ rất cao sức mạnh để chịu lực. Đó là lý tưởng để xây dựng trọng điểm, mặc dù chi phí của nó là lớn hơn so với thông đường hoặc gỗ trầm. Yamaha bàn phím phản ứng nhanh chóng đáp ứng được sự lặp lại nhanh chóng cho các phần phức tạp nhất của âm nhạc. Yamaha bàn phím chịu được lực của những nét lướt mạnh trong những đoạn nhạc cực mạnh.', NULL, 'piano-yamaha-u3a-205x205', '0000-00-00', 10, NULL, 0, 2, 1),
(28, 1, 'Piano Yamaha U30A', '75000000', 'Với chiều cao 131cm, đàn piano Yamaha U30A có âm thanh quyến rũ, lôi cuốn sự hứng khởi của người chơi.  Âm thanh tự nhiên và vẻ đẹp lộng lẫy đàn piano Yamaha U30A thực sự là lựa chọn tuyệt vời cho bạn.\r\nĐàn Piano Yamaha U30A là sản phẩm được cải tiến từ dòng sản phẩm U series của Yamaha, với những thiết kế nổi bật và những đường cong làm tăng độ dài của dây đàn. Các chi tiết của sản phẩm được làm tỉ mỉ từ những người thợ thủ công Nhật Bản kết hợp với công nghệ tiên tiến tạo cho chiếc Piano có những đột phá mới trong âm thanh.\r\n\r\nPiano Yamaha U30A được làm từ gỗ gụ tự nhiên kết hợp với màu sơn sáng bóng mang đến cho ngôi nhà của bạn sự sang trọng lịch lãm.', NULL, 'Piano-Yamaha-U30A-205x205', '0000-00-00', 12, NULL, 0, 1, 1),
(29, 1, 'piano yamaha ux3', '80000000', 'Piano Yamaha UX3 là sản phẩm nổi bật của dòng đàn piano Yamaha UX. Chỉ đứng sau UX5, piano Yamaha UX3 được thiết kế và phát triển trên nền tảng của piano Yamaha UX, dòng đàn piano cho âm thanh dày, quyến rũ và có chiều sâu. Với thiết kế cách điệu trên tấm chắn đứng phía trên tạo cho piano Yamaha UX3 âm thanh bay bổng hơn nhờ tính toán thoát tiếng và độ cao 131cm của đàn. Yamaha UX3 cũng thừa hưởng thiết kế khung đàn chữ X phía sau của dòng UX tạo độ chắc chắn và đặc sắc của dòng đàn này.\r\n\r\n', NULL, 'piano-yamaha-ux3-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(30, 1, 'Piano Yamaha UX30A', '95000000', 'Piano Yamaha UX30A là một trong những sản phẩm cao cấp nhất của dòng đàn “X series” được sản xuất tại Nhật Bản. Đây là sản phẩm xuất sắc của Yamaha.\r\n\r\nCây đàn mang tính mẫu mực về âm thanh và thiết kế.\r\n\r\nYamaha đã mang những chuẩn mực khắt khe về chất liệu để sản xuất ra cây đàn. Gỗ sử dụng cho UX30A được xử lý theo quy trình nghiêm ngặt để sản xuất cho dòng đàn cao cấp. Nhờ vậy mà bảng phát âm (soundboard) có được những phẩm chất để cho ra âm sắc hay nhất. Khung kim loại căng dây đàn được chế tạo cách đặc biệt theo tiêu chuẩn cao cấp của dòng X series cho khả năng chịu lực cao và khả năng truyền âm đến soundboard tốt nhất.\r\n\r\nPhần khung gỗ bên ngoài cũng vậy, thừa hưởng từ thiết kế của dòng X series với mặt khung sau hình chữ “X” cho khả năng ổn định giúp cho cây đàn cứng cáp và mạnh mẽ, nhờ đó các bộ phận bên trong được bảo vệ cách tốt nhất.', NULL, 'Piano-Yamaha-UX30A-205x205', '0000-00-00', 18, NULL, 0, 0, 1),
(31, 1, 'Piano yamaha ux', '57000000', 'Đàn piano Yamaha UX được sản xuất vào khoảng năm 1975-1980 với số serial dao động vào khoảng 2.000.000 – 3.200.000 (xem thêm năm sản xuất của đàn piano Yamaha).\r\n\r\nĐàn piano Yamaha UX có khá nhiều cải tiến đối với cây đàn upright so với các model đàn piano trước đó như khung đàn được chế tạo hình chữ X, thiết kế tinh xảo để cải thiện hiệu suất âm thanh, vật liệu sản xuất đàn được tiêu chuẩn hóa theo tiêu chuẩn quốc tế, nắp phím đàn được chế tạo rộng rãi hơn thuận tiện trong việc đặt nhiều sheet nhạc. Tất cả làm nên một cây đàn piano tuyệt vời từ kiểu dáng sang trọng, tinh tế tới âm thanh mẫu mực của dòng upright.', NULL, 'Piano-yamaha-ux-205x205', '0000-00-00', 20, NULL, 0, 1, 1),
(32, 1, 'piano yamaha w103', '49500000', 'Đàn Piano Yamaha W103 thuộc dòng sản phẩm thiết kế cổ điển của thương hiệu Yamaha, với đặc trưng màu gỗ sang trọng, rất thích hợp cho các không gian nhà sử dụng nội thất gỗ đồng bộ.\r\n\r\nPiano Yamaha W103 thuộc W-Series của thương hiệu đàn piano Yamaha, là những cây đàn có thiết kế màu gỗ đặc trưng. Sở hữu thiết kế thuộc dạng cổ điển nhưng cây đàn lại là một trong những cây đàn có chất âm thanh trầm ấm và chi tiết, rất phù hợp với xu hướng âm nhạc hiện nay.  Đây là một trong những sự lựa chọn mua đàn piano phục vụ việc học và giải trí với đàn của nhiều người chơi yêu thích sự cổ điển.', NULL, 'piano-yamaha-w103-205x205', '0000-00-00', 20, NULL, 0, 0, 1),
(33, 1, 'Piano Yamaha W106', '64000000', 'Dòng piano yamaha W là chữ viết tắt của Wood (Gỗ), màu sắc đặc trưng của dòng này là màu vân Gỗ, bóng mờ hoặc bóng kính. Được sản xuất hoàn toàn thủ công bởi những nghệ nhân Nhật Bản. Đàn Piano yamaha W106 với thiết kế rất tinh tế mang đậm phong cách châu âu sẽ làm cho ngôi nhà bạn thêm đẹp , trang trọng và quý tộc.\r\n\r\nĐàn Piano Yamaha W106 được sản xuất từ những năm 1980, thời kỳ Yamaha đã đạt đến đỉnh cao trong lĩnh vực sản xuất đàn piano đăc biệt trong thời gian này sản lượng gỗ chất lượng cao phong phú được nhập khẩu từ Bắc Phi rất thích hợp cho đàn piano mà cho đến nay không có bất kỳ loại gỗ nào có thể thay thế.\r\nKhông những bị cuốn hút bởi âm thanh trong sáng bay vút mà đàn Piano yamaha W106 mang lại bởi kết cấu hộp âm đàn và khung đàn đều là gỗ thông bắc mỹ một loại gỗ huyền thoại .\r\n\r\nĐặc điểm đàn Piano Yamaha W106\r\n\r\n– Thùng đàn Piano Yamaha W106\r\nNhìn tổng quát thì thùng đàn Piano Yamaha W106 có hình dáng giống chiếc hợp được dựng đứng, với các đường cạnh được mài dũa rất cứng cáp. Ngoài ra, các cây đàn piano yamaha W106  đại đa số đều được phủ lớp sơn màu đen sáng bóng. Góp phần tôn lên vẻ đẹp sang trọng cho cây đàn piano yamaha W106. Đặc biệt hơn hết, thùng đàn là bộ phần nâng đỡ rất nhiều các bộ phận khác như khung đàn…Nó được làm từ một loại gỗ lớn họ thông.\r\n– Bộ Pedals đàn Piano Yamaha W106\r\nBộ Pedals có 3 bàn đạp với 3 chức năng khác nhau: soft pedal (hoặc una corda pedal) là giảm âm thanh, tiếp đến là sostenuto pedal là pedal duy trì và cuối cùng là damper pedal (hoặc sustain pedal) là pedal vang âm. Chất liệu của bộ Pedals được đúc từ hợp kim đồng nên rất chắc chắn và màu vàng sáng bóng tạo nên vẻ sang trọng hơn cho cây đàn piano.\r\n– Bộ máy đàn Piano Yamaha W106\r\nBàn phím đàn piano yamaha W106 gồm có tổng cộng 88 phím, được làm từ chất liệu Phenolic Resin.\r\nMáy đàn phía trong đàn là một tổ hợp liên kết của các bộ phận nhỏ lại với nhau. Nguyên lý hoạt động giống như đòn bẩy và đặc điểm dễ dàng nhận biết là búa đàn. Và điều lưu ý ở đây, hầu hết các chi tiết trong máy đàn đều được chế tạo thủ công và bằng gỗ nên rất nhảy cảm với nhiệt độ và độ ẩm.\r\n– Dây đàn Piano Yamaha W106\r\nDây đàn được chế tạo bằng thép cường độ cao hội tụ yếu tố dẻo dai, ít cacbon nhất, dây có đường kính khác nhau. Tổng số lượng dây đàn trên cây đàn piano W106 là 220 dây. Tương ứng với 4 kích thước khác nhau dùng trong các cung vực khác nhau.\r\n– Soundboard đàn Piano Yamaha W106\r\nSoundboard là một tấm gỗ lớn, mỏng, được đặt dưới dây đàn và kết nối thông qua ngựa đàn. Một số nhạc cụ khác cũng có soundboad như: violin, guitar…dựa vào con ngựa đàn ta sẽ nhận biết được vị trí soundboard nằm ở đâu.\r\nCác soundboard thường được làm từ gỗ họ thông như gỗ Vân Sam Sitka, độ dày của Soundboard vào khoảng ½ inch tương đương 1.27cm, rộng khoảng 4 – 6 inch tương đương 10.16 – 15.24cm và được ghép lại với nhau để tạo thành 1 tấm soundboard lớn.\r\nBảng cộng hưởng (Soundboard) bao gồm 2 bộ phận chính là: xương sườn (Ribs) và ngựa đàn (bridge).\r\nThông số kỹ thuật đàn Piano Yamaha W106\r\n– Kích thước:\r\n+ Chiều rộng: 153 cm\r\n+ Chiều cao: 131 cm\r\n+ Chiều ngang:   65 cm\r\n– Trọng lượng:  250 kg\r\n– Phím đàn:\r\n+ Số phím đàn: 88\r\n+ Chất liệu  phím: Phenolic Resin\r\n– Pedals: Soft Pedal, Mute Pedal, Damper Pedal\r\n– Nhà sản xuất: Yamaha', NULL, 'Piano-Yamaha-W106-205x205', '0000-00-00', 10, NULL, 0, 0, 1),
(34, 1, 'Yamaha U1G', '38000000', 'Đàn piano Yamaha dòng U upright từ lâu đã là một sự lựa chọn hàng đầu cho các tổ chức giáo dục, nhạc sĩ chuyên nghiệp và các nghệ sĩ piano.\r\nNhờ thiết kế toàn diện theo tiêu chuẩn quốc tế cải thiện hiệu suất âm, độ bền của piano. Là dòng đầu tiên đặt ra tiêu chuẩn thiết kế với các kiểu dáng sang trọng . Gỗ tự nhiên giúp chiếc Piano dễ dàng thích nghi với môi trường khí hậu hai mùa nóng ẩm của Việt Nam.\r\n\r\nĐàn piano Yamaha U1G\r\n\r\nLà một trong những sản phẩm phổ biến của dòng piano Yamaha U1, đàn piano yamaha U1G có màu đen, sáng bóng rất được ưa chuộng ở thị trường Việt Nam. Với khả năng thích nghi tốt với môi trường Piano Yamaha U1G cho bạn độ ổn định cao trong việc sử dụng cây Piano. Gía cả phải chăng là ưu điểm của chiếc đàn piano yamaha U1G.\r\n\r\nViệc sở hưu một chiếc Piano trong nhà đã trở nên phổ biến trong gia đình Việt. Ngày nay việc sắm một chiếc Piano đã trở thành một xu hướng đối với tầng lớp trí thức trong xã hội. Nếu muốn chọn một cây đàn phù hợp với túi tiền và không gian gia đình nhất. Chiếc Piano đã trở thành một vật dụng kết nối gia đình bạn trong những ngày nghỉ cuối tuần. Hãy đến với chúng tôi để nhận những tư vấn miễn phí về mua đàn Piano, học đàn piano, sử dụng và cách thức sử dụng đàn piano.', NULL, 'Yamaha-U1G-205x205', '0000-00-00', 24, NULL, 0, 7, 1),
(35, 1, 'Yamaha U3H', '48500000', 'Đàn Piano Yamaha dòng U3 từ lâu đã là một sự lựa chọn hàng đầu cho các tổ chức giáo dục, nhạc sĩ chuyên nghiệp, nghệ sĩ piano,người bắt đầu học piano và biểu diễn bán chuyên nghiệp ở Việt Nam. Hơn nữa, thương hiệu đàn piano Yamaha đã trở nên quen thuộc và uy tín đối với người tiêu dùng Việt Nam.\r\n\r\nĐàn Piano cơ Yamaha U3H – một trong những cây đàn piano được người tiêu dùng bình chọn, yêu thích nhất trong các loại đàn piano Yamaha.\r\n\r\nĐàn Piano Yamaha U3H có thiết kế sang trọng với màu đen bóng, phù hợp với mọi vật liệu nội thất trong nhà, khách sạn,… với âm thanh bay bổng và âm bass dày dặn đã tạo nên một cây đàn thật hoàn hảo. Với chiều cao 131cm, đàn piano Yamaha U3H cho kiểu dáng hài hòa, âm thanh vang hay với mọi diện tích phòng của bạn.\r\nSau đây là thông số kỹ thuật của đàn piano Yamaha U3H.\r\n\r\nNhà sản xuất: Yamaha\r\nMàu: Đen\r\nSố pedal: 03\r\nNăm sản xuất: 1975-1980\r\nTrọng lượng: 250 kg\r\nKích thước: Rộng (151cm) x Cao (131cm) x Sâu (63cm)\r\nBảo hành: 5 năm\r\nSản phẩm kèm theo: miễn phí vận chuyển tận nơi nội thành TP.HCM, tặng ghế, khăn phủ phím, khăn phủ đàn và ống sưởi.', NULL, 'Yamaha-U3H-205x205', '0000-00-00', 12, NULL, 0, 0, 1),
(36, 1, 'Yamaha U3M', '58000000', 'Piano Yamaha U3M Upright là sự lựa chọn hoàn hảo cho không gian nhà, trong phòng thu. Là một trong những cây đàn piano nổi tiếng trên thế giới. Nhờ thiết kế toàn diện theo tiêu chuẩn quốc tế cải thiện hiệu suất âm, độ bền của piano. Là dòng đầu tiên đặt ra tiêu chuẩn với các kiểu dáng sang trọng.', NULL, 'Yamaha-U3M-205x205', '0000-00-00', 18, NULL, 0, 0, 1),
(37, 1, 'Yamaha W106B', '64000000', 'Dòng piano yamaha W là chữ viết tắt của Wood (Gỗ), màu sắc đặc trưng của dòng này là màu vân Gỗ, bóng mờ hoặc bóng kính. Được sản xuất hoàn toàn thủ công bởi những nghệ nhân Nhật Bản. Đàn Piano yamaha W106 với thiết kế rất tinh tế mang đậm phong cách châu âu sẽ làm cho ngôi nhà bạn thêm đẹp , trang trọng và quý tộc.\r\n\r\nĐàn Piano Yamaha W106 được sản xuất từ những năm 1980, thời kỳ Yamaha đã đạt đến đỉnh cao trong lĩnh vực sản xuất đàn piano đăc biệt trong thời gian này sản lượng gỗ chất lượng cao phong phú được nhập khẩu từ Bắc Phi rất thích hợp cho đàn piano mà cho đến nay không có bất kỳ loại gỗ nào có thể thay thế.\r\nKhông những bị cuốn hút bởi âm thanh trong sáng bay vút mà đàn Piano yamaha W106 mang lại bởi kết cấu hộp âm đàn và khung đàn đều là gỗ thông bắc mỹ một loại gỗ huyền thoại .\r\n\r\nĐặc điểm đàn Piano Yamaha W106\r\n\r\n– Thùng đàn Piano Yamaha W106\r\nNhìn tổng quát thì thùng đàn Piano Yamaha W106 có hình dáng giống chiếc hợp được dựng đứng, với các đường cạnh được mài dũa rất cứng cáp. Ngoài ra, các cây đàn piano yamaha W106  đại đa số đều được phủ lớp sơn màu đen sáng bóng. Góp phần tôn lên vẻ đẹp sang trọng cho cây đàn piano yamaha W106. Đặc biệt hơn hết, thùng đàn là bộ phần nâng đỡ rất nhiều các bộ phận khác như khung đàn…Nó được làm từ một loại gỗ lớn họ thông.\r\n– Bộ Pedals đàn Piano Yamaha W106\r\nBộ Pedals có 3 bàn đạp với 3 chức năng khác nhau: soft pedal (hoặc una corda pedal) là giảm âm thanh, tiếp đến là sostenuto pedal là pedal duy trì và cuối cùng là damper pedal (hoặc sustain pedal) là pedal vang âm. Chất liệu của bộ Pedals được đúc từ hợp kim đồng nên rất chắc chắn và màu vàng sáng bóng tạo nên vẻ sang trọng hơn cho cây đàn piano.\r\n– Bộ máy đàn Piano Yamaha W106\r\nBàn phím đàn piano yamaha W106 gồm có tổng cộng 88 phím, được làm từ chất liệu Phenolic Resin.\r\nMáy đàn phía trong đàn là một tổ hợp liên kết của các bộ phận nhỏ lại với nhau. Nguyên lý hoạt động giống như đòn bẩy và đặc điểm dễ dàng nhận biết là búa đàn. Và điều lưu ý ở đây, hầu hết các chi tiết trong máy đàn đều được chế tạo thủ công và bằng gỗ nên rất nhảy cảm với nhiệt độ và độ ẩm.\r\n– Dây đàn Piano Yamaha W106\r\nDây đàn được chế tạo bằng thép cường độ cao hội tụ yếu tố dẻo dai, ít cacbon nhất, dây có đường kính khác nhau. Tổng số lượng dây đàn trên cây đàn piano W106 là 220 dây. Tương ứng với 4 kích thước khác nhau dùng trong các cung vực khác nhau.\r\n– Soundboard đàn Piano Yamaha W106\r\nSoundboard là một tấm gỗ lớn, mỏng, được đặt dưới dây đàn và kết nối thông qua ngựa đàn. Một số nhạc cụ khác cũng có soundboad như: violin, guitar…dựa vào con ngựa đàn ta sẽ nhận biết được vị trí soundboard nằm ở đâu.\r\nCác soundboard thường được làm từ gỗ họ thông như gỗ Vân Sam Sitka, độ dày của Soundboard vào khoảng ½ inch tương đương 1.27cm, rộng khoảng 4 – 6 inch tương đương 10.16 – 15.24cm và được ghép lại với nhau để tạo thành 1 tấm soundboard lớn.\r\nBảng cộng hưởng (Soundboard) bao gồm 2 bộ phận chính là: xương sườn (Ribs) và ngựa đàn (bridge).\r\nThông số kỹ thuật đàn Piano Yamaha W106\r\n– Kích thước:\r\n+ Chiều rộng: 153 cm\r\n+ Chiều cao: 131 cm\r\n+ Chiều ngang:   65 cm\r\n– Trọng lượng:  250 kg\r\n– Phím đàn:\r\n+ Số phím đàn: 88\r\n+ Chất liệu  phím: Phenolic Resin\r\n– Pedals: Soft Pedal, Mute Pedal, Damper Pedal\r\n– Nhà sản xuất: Yamaha', NULL, 'Yamaha-W106B-205x205', '0000-00-00', 20, NULL, 0, 2, 1),
(38, 1, 'Yamaha YUX', '71000000', 'Piano Yamaha YUX là dòng sản phẩm cao cấp của Yamaha, một trong những dòng Upright Piano được người dùng ưa chuộng nhất. Piano Yamaha YUX là chiếc đàn dành cho những người muốn có một sự mới mẻ cho cây đàn piano của mình.\r\nNhững cây đàn piano “X” series của Yamaha có sự cải thiện đáng kể so với dòng U series mang đến cho bạn cây đàn piano có âm thanh tốt nhất.\r\n\r\nÂm thanh của YUX trong sáng, thanh thoát ở những note cao bổng; bass có độ dày, trầm và ấm. Khung đàn được làm hình chữ X chắc chắn và sang trọng. đàn thích hợp đặt ở phòng khách lớn, quán bar và những nơi sang trọng.', NULL, 'Yamaha-YUX-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(39, 2, 'Casio AP 26', '7800000', 'Casio AP-26 là một cây đàn piano điện hoàn hảo. Sở hữu một bộ âm thanh stereo mẫu bao gồm các dạng âm riêng biệt khi gõ phím đàn nhẹ hay mạnh, làm nổi bật sự khác biệt giữa các âm thanh được tạo ra bởi mỗi phong cách chơi khác nhau.\r\n\r\nPhím đàn được thiết kế cho một cảm giác nặng hơn với trình độ thấp và một cảm giác nhẹ hơn với trình độ cao, khi chơi giúp người chơi có cảm giác tương tự như của một piano cơ. Phím đàn không sử dụng lò xo, và hoạt động của nó được cung cấp sức nặng của búa làm cho cây đàn piano có cảm giác chân thật.\r\nThiết kế của piano điện Casio AP-26 với màu gỗ sồi giúp không gian trở nên sang trọng hơn. Sản phẩm đàn piano điện này phù hợp cho nhu cầu gia đình, phòng trà, nhà hàng sang trọng dùng để bày trí hoặc có thể dùng chúng tạo nên không gian thoải mái và trầm lắng xung quanh.', NULL, 'Casio-AP-26-205x205', '0000-00-00', 20, NULL, 0, 0, 1),
(40, 2, 'Casio PX 720', '8500000', 'Piano điện Casio PX-720 cho âm sắc và khả năng trình diễn giống như đàn grand piano.\r\n\r\nCasio PX-720 có hệ âm sắc tối đa 128 nốt. Âm sắc thế hệ mới cho tiếng piano cơ và biểu cảm. Thế hệ âm thanh mới AIF cho chất lượng âm thanh và khả năng trình diễn giống như chơi trên một cây đàn grand piano.', NULL, 'Casio-PX-720C-205x205', '0000-00-00', 15, NULL, 0, 1, 1),
(41, 2, 'Piano dien Kawai CN-21', '12000000', 'Kawai CN-21 là dòng piano điện thế hệ mới của hãng Kawai với thiết kế mới, chất lượng cao, tăng cường tính chắc chắn, bàn phím được nâng cấp, hệ thống loa âm thanh uy lực hơn.\r\n\r\nKawai CN-21 được trang bị thế hệ âm thanh mới nhất Harmonic Imaging II được tái tạo từ âm sắc piano tuyệt hảo từ đàn đại dương cầm KAWAI EX. Hệ thống action của bàn phím AHA IV-E được cải thiện trong đàn piano Kawai CN-21 cho bạn cảm giác phím trung thực hơn.\r\n\r\nPedal của Kawai CN-21 được trang bị chức năm pedal một nửa (half-pedal).\r\n\r\nChức năng thực hành các bài học cũng được trang bị trên đàn từ sách học đàn nổi tiếng Alfred.', NULL, 'cn21c-205x205', '0000-00-00', 20, NULL, 0, 0, 1),
(42, 2, 'Kawai CA 91', '12000000', 'Piano điện Kawai CA-91 là piano điện đầu tiên có bảng phát âm (soundboard) làm bằng gỗ thông vân sam giống như đàn piano cơ.\r\nĐặc điểm nổi trội của CA-91 Concert Artist là cuộc cách mạng của hệ thống loa từ bảng phát âm mang thương hiệu Kawai. Bảng phát âm được làm từ gỗ tự nhiên tái tạo trung thực âm sắc ấm áp và tự nhiên của một cây đàn cơ. Các kỹ sư của Kawai mất 4 năm để nghiên cứu ra hệ thống này.\r\n\r\nPiano điện Kawai CA-91 có âm trầm rất quyến rũ với độ sâu và dày điển hình của đàn grand piano cơ mà hệ thống loa thông thường không thể chuyển tải được.', NULL, 'Kawai-CA-91-205x205', '0000-00-00', 17, NULL, 0, 0, 1),
(43, 2, 'Kawai CN 22C', '22000000', 'Kawai CN-22 trang bị hệ tiếng piano lấy mẫu Harmonic Imaging mới nhất. Đây là cây đàn piano điện có hệ tiếng rất hay của Kawai.\r\n\r\nĐàn được thiết kế nhỏ gọn (compact) nhưng vẫn phát huy được đầy đủ tính năng của cây piano điện cao cấp.', NULL, 'Kawai-CN-22C-205x205', '0000-00-00', 25, NULL, 0, 0, 1),
(44, 2, 'Kawai PW 750', '10000000', 'Là một trong những cây đàn piano hấp dẫn nhất từng được thực hiện bởi Kawai. Đàn Piano điện Kawai PW-750 có vóc dáng mạnh mẽ, đương đại, thu hút được nhiều chú ý từ bất cứ nơi nào bạn đặt nó.\r\nĐàn Piano điện Kawai PW-750 được trang bị một màn hình hiển thị kỹ thuật số, 3pedals, chức năng ghi âm và phát lại những bản nhạc đã chơi.\r\n\r\nĐiều làm cho đàn piano này trở nên đặc biệt là Đàn Piano điện Kawai PW-750 có phím bằng gỗ thiên nhiên. Trong khi hầu hết các loại đàn piano điện khác có phím bằng nhựa. Các phím bằng gỗ bảo đảm sẽ tạo ra cho người chơi những cảm ứng tự nhiên và cảm nhận tuyệt vời trong khi chơi đàn piano điện Kawai PW-750.', NULL, 'Kawai-PW-750-205x205', '0000-00-00', 12, NULL, 0, 0, 1),
(45, 2, 'Korg FC 500', '15000000', 'Bán đàn piano điện Korg FC-500 giá rẻ tại TPHCM, âm thanh hay đặc biệt là tiếng piano, đàn có chức năng trộn hai âm sắc và nhiều hiệu ứng âm thanh.', NULL, 'Korg-FC-500-205x205', '0000-00-00', 24, NULL, 0, 0, 1),
(46, 2, 'Piano Casio AP 30', '9000000', 'Piano điện Casio AP-30', NULL, 'Piano-Casio-AP-30-205x205', '0000-00-00', 20, NULL, 0, 0, 1),
(47, 2, 'Piano Casio AP 65R', '22000000', 'Piano điện Casio AP-65R sử dụng công nghệ mô phỏng bàn phím kiểu búa gõ tự nhiên giống như đàn grand piano. Các nốt ở phần thấp của bàn phím sẽ có độ nặng nhiều hơn các nốt ở phần cao giống như bàn phím của đàn piano cơ.\r\n\r\n', NULL, 'Piano-Casio-AP-65R-205x205', '0000-00-00', 10, NULL, 0, 1, 1),
(48, 2, 'Piano Casio PX 700', '8000000', 'Piano điện Casio PX-700 có thiết kế nhỏ gọn, linh hoạt, là sản phẩm đáp ứng nhu cầu luyện tập. học piano dành cho nguời mới bắt đầu hoặc những trung tâm lớp nhạc bởi những điểm mạnh về kinh tế và sự tiện ích.\r\nĐặc điểm nổi bật của piano điện Casio PX-700\r\n\r\n– Sử dụng nguồn âm Linear Morphing System AIF mô phỏng âm thanh lớn hơn, có độ mạnh và nhẹ một cách tự nhiên hơn. Nó thường được dùng cho những buổi hòa nhạc nổi tiếng trong cây đàn Grand Piano, bên cạnh đó, công nghệ morphing được dùng để thay đổi âm thanh của các note để tạo nên sự liên tục. Kết quả là sẽ có sự chuyển đổi một cách êm ái, nhẹ nhàng và hoàn toàn tự nhiên từ nhẹ sang mạnh đem lại sự diễn đạt hoàn hảo mà điều đó rất khó có được khi chơi với một piano điện thông thường.\r\n\r\n– Bộ cảm biến Tri-sensor Scaled Hammer Action Keyboard cải tiến kết hợp 3 trong 1 cho bạn được cảm giác như đang chơi một cây Grand Piano. Với bàn phím nặng cho những nốt trầm và nhẹ cho những nốt cao tạo cảm giác như chơi đàn thật. Hoạt động của phím áp dụng theo nguyên tắc của bộ máy đàn Grand Piano bằng việc sử dụng chức năng của các đầu cần (các búa gõ trong đàn piano)\r\n\r\n– Đặc biệt , Piano điện Casio PX-700 còn có chế độ  Duet mode  cho phép hai người chơi đàn cùng một lúc ở hai khoảng của đàn mà không ảnh hưởng gì đến chất lượng của bài học. Rất hiệu quả cho hai người muốn giao lưu tập luyện cùng nhau.', NULL, 'Piano-Casio-PX-700-205x205', '0000-00-00', 9, NULL, 0, 1, 1),
(49, 2, 'Piano Casio PX 735', '28000000', 'Piano điện Casio PX-735 có hàng loạt các tính năng tuyệt vời với công nghệ âm thanh tiên tiến và thiết kế mang tính đột phá.\r\nMột số tính năng khác của đàn Piano điện Casio PX-735\r\n\r\n– Số phím: 88\r\n\r\n– Số tiếng đàn: 18 (piano, organ, violin, guitar, saxophone, trumpet…)\r\n– Số bài nhạc có sẵn: 60\r\n– Chức năng tự học: Cho phép tắt phía bên trái hoặc bên phải của bàn phím thuận lợi cho việc học\r\n– Chế độ cặp đôi: Chia đôi bàn phím như 2 chiếc piano độc lập, thích hợp cho giáo viên và người học chơi đàn cùng với nhau chia đôi bàn phím như 2 chiếc piano độc lập\r\n– Đầu thu: Ghi âm và phát lại những gì đã chơi\r\n– Cổng USB: Giúp dễ dàng giao tiếp với máy tính\r\n– Giao tiếp MIDI: Có thể giao tiếp với các nhạc cụ hay thiết bị MIDI khác\r\n– Nguồn âm AIF (Acoustic and Intelligent Filtering system): Công nghệ xử lý mới nhất tạo ra âm thanh tự nhiên và chân thật, cảm giác như đang nghe những nhạc cụ acoustic.\r\n– Phím phân bổ độ nặng: Nặng hơn ở những nốt trầm và nhẹ hơn ở những nốt cao, cảm giác như chơi đàn piano.\r\n– Hiệu ứng vang âm dương cầm: Tái tạo hiệu ứng vang âm như đàn dương cầm\r\n– Phản ứng với cường độ va chạm: Giống với Piano dây, keyboard cho lớn tiếng khi nhấn mạnh và nhỏ tiếng khi nhấn nhẹ\r\n– Bộ nhớ: Mọi thiết lập đều có thể lưu lại\r\n– Chức năng khóa: Có thể khóa những thiết lập để tránh những thay đổi ngoài ý muốn', NULL, 'Piano-Casio-PX-735-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(50, 2, 'Piano dien Casio AP 45', '10000000', 'Piano Casio AP-45 không chỉ cải thiện về kiểu dáng mà còn cả chất lượng âm thanh mang đến cho người chơi, người nghe những xúc cảm. Các sản phẩm của Casio đều hướng tới sự tiện dụng. Với mức giá hợp lí Casio đặc biệt Casio AP-45 luôn được khách hàng ưa chuộng.\r\n\r\nĐàn piano điện Casio AP-45 nổi bật với hệ thống âm thanh tinh tế, màu sáng thanh lịch, có giá bán rẻ phù hợp với người mới học đàn như học sinh.', NULL, 'Piano-dien-Casio-AP-45-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(51, 2, 'Piano dien Casio PS 3000', '9000000', 'Trang chủ » Sản phẩm » Piano điện Casio PS-3000\r\n\r\n\r\nPiano điện Casio PS-3000\r\n9,000,000 đ\r\n10,000,000 đ\r\n71 Xuân Hồng, P.12, Q.Tân Bình, TP.HCM\r\n624 Điện Biên Phủ, P.11, Quận 10, TP.HCM\r\nHotline: 0988 019 900 – 0906 680 608\r\nPiano điện Casio PS-3000 là piano điện có kiểu dáng nhỏ gọn, phù hợp với không gian hẹp nhưng vẫn đáp ứng được nhu cầu tập luyện và giải trí.\r\nKiểu dáng của Casio PS-3000 được thiết kế tinh tế với gam màu vàng nhạt sáng.\r\n\r\nBảo hành 12 tháng\r\nNhà sản xuất: Casio\r\nNăm sản xuất: 2006\r\nXuất xứ: Nhật Bản\r\nTặng ghế và miễn phí vận chuyển trong nội thành TP.HCM\r\nLiên hệ: 0988 019 900', NULL, 'Piano-dien-Casio-PS-3000-205x205', '0000-00-00', 12, NULL, 0, 0, 1),
(52, 2, 'Piano dien Casio PX 750', '27000000', 'Piano điện Casio PX-750 cải tiến với nhiều công nghệ mới, được phát triển bởi Casio thể hiện sự kỹ lưỡng đến từng chi tiết trong việc thể hiện những âm sắc cộng hưởng phong phú vốn có của đàn piano acoustic, kết hợp thiết kế phủ gỗ thông màu trắng sáng cho không gian sang trọng.\r\nĐặc điểm nổi bật đàn Piano điện Casio PX-750\r\n\r\n– Nguồn âm thanh thế hệ mới AiR cho nhiều mẫu âm chất lượng\r\n\r\n– Hệ thống búa thế hệ II âm vang độc đáo của Grand Piano\r\n\r\n– Mô phỏng cộng hưởng âm thanh của dây đàn Grand Piano\r\n\r\n– Thể hiện chính xác thời gian nhấn và thả của ngón tay trên phím đàn\r\n\r\n– Phím đàn với chất liệu tương tự gỗ mun với 4 mức điều chỉnh cho nặng nhẹ\r\n\r\n– Tích hợp 60 bài học Piano cho người không chuyên và chuyên nghiệp\r\n\r\n– Thu âm 5.000 nốt nhạc dễ dàng lưu trữ các tác phẩm biểu diễn.', NULL, 'Piano-dien-Casio-PX-750-205x205', '0000-00-00', 20, NULL, 0, 0, 1),
(53, 2, 'Piano dien Casio PX 760', '13000000', 'Đàn piano điện Casio PX-760 mang thiết kế gọn, nhẹ của dòng piano điện Privia Casio, khiến PX-760 trở thành cây đàn piano điện dành cho học tập, giải trí, áp dụng cho những không gian linh hoạt.\r\n1. Các tính năng nổi trội trên piano điện PX-760\r\n– Nguồn âm thanh Morphing AiR đa chiều\r\n\r\n– Bàn phím hoạt động đầu cần ba cảm biến II mới phản ứng bằng cách kết hợp một hệ thống với 3 cảm ứng dò chạm bàn phím liên tiếp\r\n\r\n– Phím đàn Mô phỏng mun đen và trắng ngà mang lại cảm giác và cảm nhận bề mặt sang trọng cũng như mượt mà khi chạm vào bàn phím đại dương cầm\r\n\r\n– Cộng hưởng damper có 4 mức độ điều chỉnh\r\n\r\n2. Piano điện privia PX-760 với các tính năng linh hoạt\r\n– 18 âm sắc chất lượng cao, bao gồm các âm của đại dương cầm được lấy mẫu theo kiểu âm thanh nổi\r\n\r\n– Các hiệu ứng kỹ thuật số: âm vang (4 kiểu), hợp xướng (4 kiểu), sáng, DSP (cài sẵn một số âm)\r\n\r\n– Tính năng bài học sử dụng 60 bài hát trong Thư viện nhạc cộng thêm 10 bài hát do người chơi cài đặt vào\r\n\r\n– Hai nút GRAND PIANO và nút ELEC PIANO để truy cập ngay lập tức\r\n\r\n– Thiết bị ghi: 2 bè x 1 bài, khoảng 5.000 nốt nhạc\r\n\r\n– Cổng ổ đĩa flash USB\r\n\r\n– TỰ ĐỘNG TẮT NGUỒN Tiết kiệm điện', NULL, 'Piano-dien-Casio-PX-760-205x205', '0000-00-00', 17, NULL, 0, 0, 1),
(54, 2, 'Piano dien Kawai CA 1000', '12000000', 'Piano điện Kawai CA-1000 GP là dòng sản phẩm cao cấp của thương hiệu piano điện Kawai. Được sản xuất vào năm 2008 tại Nhật Bản, Kawai CA-1000 GP hội tụ đầy đủ những công nghệ để tạo nên một cây piano điện đẳng cấp rất cao. Âm sắc của CA-1000 GP giống với tiếng của đàn grand piano hơn bao giờ hết.\r\n\r\nThiết kế của đàn CA-1000 GP rất tinh tế với màu gỗ bóng, có hai chân phía trước, giá sách mô phỏng theo kiểu của đàn grand piano.\r\n\r\nĐây là dòng sản phẩm rất độc đáo, đúng phong cách là piano điện sản xuất tại Nhật.', NULL, 'Piano-dien-Kawai-CA-1000-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(55, 2, 'Piano dien Kawai L5', '13000000', 'Piano điện Kawai L5 có thiết kế gọn nhẹ, màu vàng gỗ tự nhiên tinh tế, là cây đàn phù hợp cho thành viên có nhu cầu học tập, luyện ngón. Cũng như phù hợp cho người chơi có không gian nhỏ hẹp, đặc biệt các bạn sinh viên ở thuê hay có kinh tế thấp thì chọn Kawai L5 là một sự lựa chọn sáng suốt và hoàn hảo.\r\nKawai L5 được sản xuất vào tháng 5 năm 2008, là model khởi động lại danh tiếng cho dòng piano điện “L” của Kawai. Nó được trang bị hệ thống phím cảm ứng bằng gỗ như piano acoustic thứ thiệt, có chiều cao như cây piano acoustic, nhưng được thu gọn phù hợp cho mọi không gian.\r\n\r\nMột số đặc điểm nổi bật của piano điện Kawai L5\r\n\r\n– Mang đầy đủ đặc điểm truyền thống của dòng L series Kawai là nhỏ gọn, hiện đại\r\n\r\n– Có tính năng touch – cảm ứng như cây đàn piano gỗ thứ thiệt cùng với hệ thống AWA\r\n\r\n– Được đặt cân bằng 4 loa bass treble 13cm trong hộp đảm bảo mang lại chất lượng âm thanh phong phú\r\n\r\n– Sử dụng bộ lọc kỹ thuật số cho gia điệu mượt mà\r\n\r\n– 88 phím chuẩn bằng gỗ như cây đại dương cầm chuyên nghiệp\r\n\r\n– Ghi âm, kết nối thiết bị ngoài, tiêu thụ điện năng ít.', NULL, 'Piano-dien-Kawai-L5-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(56, 2, 'Piano dien Kawai PN 2C', '10000000', 'Kawai PN-2 được thiết kế nhỏ gọn, kiểu dáng nhỏ nhắn nhưng vững chắc với âm thanh đậm chất trung thực đặc trưng của dòng piano điện Kawai.\r\nVới bàn phím Hammer Piano điện KAWAI PN-2 tạo cho bạn một cảm giác rất nhẹ nhàng khi sử dụng. Hệ thống âm thanh với công nghệ PW cho âm thanh sắc nét, rõ ràng.\r\n\r\nThông số của đàn piano điện Kawai PN-2:\r\n88 phím bàn phím nâng cao Hammer hành động IV\r\n15 nhạc chuông\r\n96 âm sắc, âm thanh\r\nTổng công suất lưu trữ Khoảng 7.500 âm thanh ghi âm\r\nDamper pedal, mềm mại, sostenuto\r\nHiệu ứng reverb × 3 loài\r\nChức năng kép âm thanh, tùy chọn chuyển vị, điều chỉnh, nhịp\r\n40 bài hát chọn lọc từ các buổi hòa nhạc chất lượng\r\nCó cổng MIDI (IN / OUT)\r\nCông suất 16W x 2\r\nLoa 12cm × 2\r\nMức điện áp AC100V, 50 / 60Hz\r\n45W điện năng tiêu thụ\r\nGỗ anh đào, cẩm lai( PN2C)\r\nCó phụ kiện, tai nghe\r\nKích thước / Trọng lượng 139W x 42.5D × 82H (cm) /39.5kg', NULL, 'Piano-dien-Kawai-PN-2C-205x205', '0000-00-00', 15, NULL, 0, 0, 1),
(57, 2, 'Piano dien Kawai PW 700', '8500000', 'Đàn còn rất đẹp, âm thanh hay, các chức năng hoạt động tốt không khác gì một cây đàn mới.\r\nĐàn piano điện Kawai PW-700 được thiết kế hiện đại đáp ứng cho cả âm nhạc cổ điển và hiện đại. Mỗi cây đàn piano kawai được tạo ra từ các thợ thủ công kiên nhẫn, sáng tạo và tỉ mỉ. Bằng đôi bàn tay khéo léo họ đã bắt đầu từ những chất liệu tốt nhất để làm nên cây đàn piano tốt nhất. Nhưng chính tình yêu và đam mê mới làm cho cây đàn piano thể hiện được những cung bậc âm thanh thú vị của cuộc sống.', NULL, 'Piano-dien-Kawai-PW-700-205x205', '0000-00-00', 22, NULL, 0, 1, 1);
